package com.icaroerasmo;import java.io.IOException;import java.nio.file.Path;import java.nio.file.Paths;import com.icaroerasmo.service.FaceRecognitionService;import org.bytedeco.opencv.opencv_face.*;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.CommandLineRunner;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;@SpringBootApplicationpublic class JavaRtspFaceRecognition implements CommandLineRunner {    @Autowired    private FaceRecognitionService faceRecognitionService;    private static final Path DATASET = Paths.get("trained_dataset.xml");    public static void main(String[] args) {        SpringApplication.run(JavaRtspFaceRecognition.class, args);    }    @Override    public void run(String... args) throws Exception {        FaceRecognizer faceRecognizer;        if(DATASET.toFile().exists()) {            faceRecognizer = faceRecognitionService.load();        } else {            faceRecognizer = faceRecognitionService.train(args[0]);        }        final Object[] output = faceRecognitionService.test(faceRecognizer, args[1]);        final String label = faceRecognizer.getLabelInfo((Integer) output[0]).getString();        final Double score = (Double) output[1];        System.out.println("Predicted label: " + label.substring(0, label.length()-1) + " confidence: "+ score);    }}